const storName = {
    score: [],

}

const reducers = (state = storName, action) => {
    switch (action.type) {
        case "ADD":
            // console.log(action.datas.num)
            // console.log(state.score.map(a => a.num))
            if (state.score.length == "0") {
                Object.assign(state, {
                    score: state.score.concat([action.datas])
                });
            }
            else{
                state.score.forEach(Arrays => {
                    console.log(Arrays)
                    Object.assign(state, {
                        score: state.score.concat([action.datas])
                    });
                });
            }

        default:
            return state
    }
}

export default reducers;
