const storName = {
    score: [],

}

const reducers = (state = storName, action) => {
    switch (action.type) {
        case "ADD":

            if (state.score.map(a=>a.num) != []) {
                //console.log(action.datas.num)
                //console.log(state.score)
                return Object.assign(state, {
                    score: state.score.concat([action.datas])
                })
            }
            else if (state.score == []){
                console.log(state.score)
            }

        default:
            return state
    }
}

export default reducers;
