const storName = {
    score: [],

}

const reducers = (state = storName, action) => {
    switch (action.type) {
        case "ADD":

            if (state.score.map(a=>a.num).toString() == action.datas.num.toString()) {
                console.log(action.datas.num)
                console.log(state.score)
                return Object.assign(state, {
                    score: state.score.concat([action.datas])
                })
            }

        default:
            return state
    }
}

export default reducers;
