import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Input, Button, Icon, Row, Col } from 'antd';
class Person extends Component {
    constructor() {
        super()
        this.state = {
            person: []
        }
    }
    Search = () => {
        //console.log(e.target.value)
        let hn = document.getElementById('hn').value,
            type = "Enter"
        fetch("http://192.168.101.240:6068/web_api/visit_number.php", {
            method: "POST",
            body: JSON.stringify({
                type: type,
                hn: hn
            })
        })
            .then(res => res.json())
            .then(res => {
                this.setState({
                    person: res
                })
            })
    }
    render() {
        return (
            <Row gutter={8}>
                <Col md={{ span: 3 }}>
                    ค้นหาผู้ป่วย:
                </Col>
                <Col md={{ span: 4 }}>
                    <Input placeholder={"HN"} id="hn" />
                </Col>
                <Button onClick={this.Search}><Icon type="search" />search</Button>
            </Row>
        )
    }
}

export default connect()(Person);
